import React, { Component } from 'react';
import './App.css';

class Timer extends Component {
  render() {
    return (
      <div>
        <h1>{this.props.minutes}:{this.props.seconds}</h1>
      </div>
    )
  }
}

class TimerInput extends Component {
  render() {
    return (
      <div>

      </div>
    )
  }
}

class TimerControls extends Component {

  countDown = () => {
    // while (this.props.time > 0) {
      window.setTimeout(
        this.setState({
          time: this.props.time - 1
        }),
        1000
      )
    // }
  }

  render() {
    return (
      <div>
        <button onClick={this.countDown} >Start</button>
      </div>
    )
  }
}

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      time: 124
    }
  }
  render() {
    return (
      <div className="App">
        <header>Pomodoro</header>
        <Timer minutes={Math.floor(this.state.time / 60)} seconds={this.state.time % 60} />
        <TimerControls time={this.state.time} />
      </div>
    );
  }
}

export default App;
