//       </div>
//     )
//   }
// }

class TimerControls extends Component {

  render() {
    return (
      <div>
        <button onClick={this.props.countDown} >Start</button>
      </div>
    )
  }
}

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      time: 125
    }
  }

  countDown = () => {
    while (this.props.time > 0) {
      setTimeout(
        this.setState({
          time: this.state.time - 1
        }),
        1000
      )
    }
  }

  render() {
    return (
      <div className="App">
        <header>Pomodoro</header>
        <Timer minutes={Math.floor(this.state.time / 60)} seconds={this.state.time % 60} />
        <TimerControls countDown={this.countDown} />
      </div>
    );
  }
}

export default App;
